package it.polimi.se2.meteocal.mail;


/**
 *
 * @author andreagulino
 */
public class EmailTemplate {
        
public static String header = "<!DOCTYPE html><html><body style='margin:0'> <div style='background: black; height: 60px; width: 100%'> <a href='http://localhost:8080/meteocal/' style='text-decoration:none'> <img src='";
public static String header1 = "' style='height:40px; float:left;padding:10px;'/> <span style='font-size:30px; color:white; line-height:60px; margin-left:20px; font-family: 'Helvetica Neue''>MeteoCal</span> </a> </div><div style='height:300px; font-size:18px;background-color:#f5f5f5; padding:20px'>";

public static String footer = " </div><div style='height:20px; padding:4px;width:100%; background-color:black; color:white;'> &copy; 2015 MeteoCal - Please do not reply </div></body></html>";

public static String icon = "data:image/jpeg;base64,";
/**
 * It prepares and gets the email template 
 * @param content : the message that we want to send
 * @return email template
 */
public static String getTemplate(String content) {
    return header+icon+header1+content+footer;
}

}
